INSERT INTO profiles
(name, name_latin, name_english, description_species, ordo_id, food, weight_unit, length_unit, red_book)
VALUES
('чернозобая гагара', 'Gavia arctica L.', '', 'Жизнь гагар неразрывно связана с водоемом. Они прекрасно плавают и ныряют, оставаясь под водой до трех минут. На суше же гагары беспомощны, ходить совсем не могут и передвигаются ползком, лежа на груди и отталкиваясь лапами и крыльями. В брачный период гагары издают протяжный, стонущий крик, похожий на плач ребенка или скрип дерева, а, подзывая птенцов, каркают, как вороны, или кричат «га-га- гагарра».', 1, 1, 3, null, null),
('краснозобая гагара', 'G. sfeiiafa Pont', '', 'Жизнь гагар неразрывно связана с водоемом. Они прекрасно плавают и ныряют, оставаясь под водой до трех минут. На суше же гагары беспомощны, ходить совсем не могут и передвигаются ползком, лежа на груди и отталкиваясь лапами и крыльями. В брачный период гагары издают протяжный, стонущий крик, похожий на плач ребенка или скрип дерева, а, подзывая птенцов, каркают, как вороны, или кричат «га-га- гагарра».', 1, 1, 2, NULL, NULL),
('большая поганка, или чомга', 'Podiceps crlsfaius L.', '', 'Чомга характеризуется крупными размерами, беловатой спереди шеей, темной черновато-бурой окраской спины и наличием «воротничка» из удлиненных, ржавого цвета перьев, обрамляющих бока головы и затылок; на затылке торчат два черных хохолка, образующих «рожки», которые сливаются с «воротничком».', 2, 1, 1.5, NULL, NULL),
('серощекая поганка', 'P. griseigena Bodd', '', 'Серощекая поганка несколько мельче чомги (вес до 0,7 кг). «Воротник» и хохолок у нее развиты слабо. Бока и передняя сторона шеи рыжие, щеки и низ головы дымчато-серые.', 2, 1, 0.7, NULL, NULL),
('рогатая, или красношейная поганка', 'P. aurifus L.', '', 'Рогатая поганка значительно мельче предыдущих видов (вес до 0,5 кг). Передняя сторона шеи, зоб и бока яркорыжие, на голове черный «воротник» и рыжеватые «рожки»; от клюва к ушам идут светло-рыжие полоски, верх тела, крылья и голова черные.', 2, 1, 0.5, NULL, NULL),
('розовый пеликан', 'Pelicanus onocrotalus L', '', '', 3, NULL, NULL, NULL, NULL),
('большой баклан', 'Phalacrocorax carbo ', '', '', 3, NULL, NULL, NULL, NULL),
('выпь', 'Botaurus stellaris L.', '', 'Окраска оперения выпи настолько сливается с окружающим фоном, что заметить затаившуюся в тростниках птицу трудно даже с очень близкого расстояния.', 4, NULL, NULL, NULL, NULL),
('серая цапля', 'Ardea cinerea L.', '', '', 4, NULL, NULL, NULL, NULL),
('Лебедь-кликун', 'Cygnus cygnus L.', '', 'Ниболее крупная (до 13 кг весом) и самая красивая птица из отряда пластинчатоклювых. Оперение снежно-белое. Клюв ровный, без вздутия и наростов. Основание его желтое, конец черный. Молодые птицы отличаются сероватым цветом оперения', 5, 2, 15, NULL, NULL),
('гуменник', 'A. tabalis Lath', '', 'у гуменника клюв черный, с желтым или оранжевым пояском посередине', 5, 3, NULL, NULL, NULL),
('Кряква', 'Anas platyrhynchos L.', '', 'По внешнему виду и окраске кряква похожа на обычную домашнюю утку,  Летящая кряква отличается от других уток светлым низом крыльев, белой полоской, окаймляющей ярко-фиолетовое крыловое зеркальце, и характерным посвистом («вить-вить-вить-вить»), который производят крылья, рассекающие воздух. Как и другие «благородные» (речные) утки, кряквы хорошо плавают, но плохо ныряют, поэтому кормятся преимущественно на мелководных участках водоемов, а иногда и на берегу.', 5, 4, 1.5, NULL, NULL),
('Чирок-свистун', 'Anas сгесса L.', '', 'Самая мелкая из гнездящихся в Карелии уток. Вес колеблется в пределах от 200 до 450 г, длина тела — 32 — 41 см. Селезень в брачном наряде очень красив. Голова ржаво-каштановая с контрастной синевато-зеленой полосой, идущей от глаз к затылку. Спина и бока буровато-серые с темной поперечной струйчатостью. Зеркальце ярко-зеле- ное с черным.', 5, 4, 0.45, 40, NULL),
('Серая утка', 'Anas sfrepera L', '', 'Отличается от кряквы несколько меньшими размерами, довольно однообразной серовато-бурой окраской оперения с характерным белым «зеркальцем» на крыле и более узким и коротким клювом.', 5, NULL, NULL, NULL, NULL),
('Свиязь', 'Anas penelope L.', '', 'По внешнему облику напоминает крякву, но отличается меньшими размерами (550 — 850 г.) и окраской. У селезня в брачном наряде лоб и темя желтоватые, а остальная часть головы и шея рыжевато-каштановые; спина и бока серые, с белой и темной струйчатостью. На крыле яркое зеленое «зеркальце», обрамленное спереди и сзади черными полосами, перед ним белое пятно. Самка круглый год носит скромный серовато-коричневый, с темной рябью сверху и белый снизу наряд; зеркальце на крыле черновато-бурое.', 5, 2, 0.85, NULL, NULL),
('Шилохвость', 'Anas acuta L.', '', 'От других речных уток отличается стройным телом, длинной и тонкой шеей и сильно удлиненным острым хвостом («шилом»). Встречаются шилохвости по всей Карелии, но на гнездовье немногочисленны (2— 3 пары на 1000 га подной площади), и основная их масса весной пролетает дальше на север. Появляются эти утки в середине апре- ля — начале мая, сразу же разбиваются на пары и при< (умают к токованию. Во время брачных игр селезень то кружит над самкой, то опускается к ней и расхаживает по юмле, закинув голову и распустив одно крыло. При этом он быстро проводит клювом по стержню махового пера, производя громкий дребезжащий звук.', 5, 4, NULL, NULL, NULL),
('Чирок-трескунок', 'Anas querquedula L.', '', 'Отличается от свистунка белыми снаружи стержнями маховых перьев (у свистунка они бурые) и одноцветным (без черного окаймления) «зеркальцем». В Карелии трес- кунок редок и встречается главным образом в южных районах. Селится в поймах рек и ручьев, по берегам лесных озер и на заболоченных лугах, поросших кустарником.', 5, 2, NULL, NULL, NULL),
('Широконоска', 'Anas clypeata L.', '', 'Главные отличительные признаки — длинный, сильно расширенный на конце клюв и серо-голубая окраска крыльев.', 5, 2, NULL, NULL, NULL),
('Гоголь', 'Bucephala clangula L.', '', 'Типичный представитель нырковых уток, имеет ряд характерных для этих птиц признаков: укороченное туло- мище, широкую кожистую лопасть на заднем пальце, большую голову на сравнительно короткой шее, массивный клюв; металлический блеск на крыловом «зеркальце» отсутствует. Плавающий нырок погружается в воду значительно глубже, чем речные утки. Над водой видны только голова с втянутой шеей и передняя часть спины. Гоголи — отличные ныряльщики и основную часть пищи добывают под водой. Самец в брачном оперении очень эффектен. Голова, передняя часть шеи, спина и хвост черные, с зеленоватым отливом; низ тела белый, такого же цвета большое пятно на щеке и «зеркальце» на крыльях. У самки и селезня в летнем пере голова коричневая, зоб и бока серые, брюшко белое, спина темно-бурая.', 5, 2, NULL, NULL, NULL),
('Красноголовый нырок', 'Aythya ferina L„', '', 'Весной у селезня голова каштаново-красная, грудь, шея и хвост черные, бока и «зеркальце» на крыльях белые, а остальная часть тела буровато-серая, с хорошо выраженным струйчатым рисунком. Самец и самка летом рыжевато-бурые с беловатым брюшком и темными поперечными пятнами на боках. Вес от 700 до 1300 г.', 5, 5, 1.3, NULL, NULL),
('Хохлатая чернеть', ' Ayfhya fuligula L.', '', 'Селезень в брачном наряде черного цвета с белыми боками и брюшком; «зеркальце» на крыльях тоже белое. Удлиненные перья на затылке образуют хохолок. Самка окрашена почти так же, как и селезень, только все черные тона заменены рыжевато-бурыми. Такую же окраску имеют самцы в летнем пере.', 5, 1, NULL, NULL, NULL),
('Морская чернеть', 'Aythya marila L.', '', 'От хохлатой чернети отличается более крупными размерами, меньшей выраженностью хохолка и светло-серой окраской спины с темной поперечной струйчатостью', 5, 6, NULL, NULL, NULL),
('Обыкновенная гага', 'Somaferia mollissima L.', '', 'Самая крупная из наших нырковых уток. Самцы достигают веса 3 кг, самки — 2,5 кг. Брачный наряд селезня сверху белый, а снизу черный. Черного цвета пятно на темени, затылок и щеки зеленоватые, грудь охристо-палевая. Утка и селезень в летнем пере ржаво-бурые, с темным рисунком из мелких крапин и полосок.', 5, 7, 3, NULL, NULL),
('Морянка', 'Clangula hyemalis L.', '', 'Селезень в брачном наряде имеет очень своеобразный вздернутый хвост с длинными нитевидными перьями. Бока головы и низ тела белые, грудь черная; спина и крылья темно-бурые, с беловатыми перьями на плечах. Самка сверху темно-бурая, бока дымчатые, низ белый.', 5, NULL, NULL, NULL, NULL),
('Синьга', 'Melanitta nigra L.', '', 'Селезень совершенно черного цвета. Клюв — с оранжевой полосой посередине и наростом в основании надклювья. Самка темно-бурая, с более светлыми щеками, грудью и брюшком. Гнездится только в северной Карелии, а на остальной территории встречается на пролете. Селится по берегам моря или на больших озерах с обширными чистыми плесами.', 5, NULL, NULL, NULL, NULL),
('Турпан', 'Melanitta fusca L', '', 'Селезень этой утки, подобно синьге, черного цвета, но в отличие от нее имеет на крыле чисто-белое «зеркало» и белое пятно около глаза.', 5, NULL, NULL, NULL, NULL),
('большой крохаль', 'Mergus merganser I.', '', 'Большой крохаль значительно крупнее двух других видов (вес до 2 кг). У самца в брачном наряде черные, с зеленоватым отливом голова, верх шеи и спина, а остальные части туловища — белые. Самка и самец в летнем пере окрашены скромнее, в рыжевато-бурые тона.', 5, NULL, 2, NULL, NULL),
('длинноносый крохаль', 'М. serrator L.', '', 'Длинноносый крохаль похож на большого, только несколько меньше его (вес 700— 1400 г), имеет более длинный клюв с 17— 19 зубцами по краю надклювья (у предыдущего вида их не более 15), и зоб у него не белый, а рыжевато-бурый, с черными пестринами. На затылке двойной хохол из длинных взъерошенных перьев.', 5, NULL, 1.4, NULL, NULL),
('луток крохаль', 'М. albellus L.', '', 'Луток — самый мелкий из наших крохалей: вес его не превышает 790 г. Клюв относительно короткий (до 30 мм), зубчики тупые. Селезень весной почти весь белый, только пятна вокруг глаз, верхняя часть спины, маховые перья и полосы на боках почти черные. Самка и самец в летнем наряде невзрачные — буровато-серые, с рыжей головой и белым низом.', 5, NULL, 0.79, NULL, NULL),
('ястреб перепелятник', 'Accipifer nisus L.', '', 'Ястребов отличают по сравнительно широким коротким крыльям и длинному хвосту. Нижняя часть тела у них серовато-белая, с темными поперечными полосами. Верх тела окрашен в серовато-бурый цвет. Охристые каемки перьев спины и кроющих перьев крыла образуют своеобразный рисунок. Самцы всегда значительно меньше самок. Так, самки ястреба-перепелятника весят до 300 г, а самцы — до 170 г. Оба вида — типично лесные хищники, отличающиеся быстрым маневренным полетом. Относительно длинные неоперенные ноги приспособлены схватывать жертвы в ветвях деревьев и кустарников. Ястреб может ловить добычу на лету и с земли. Ястреб-перепелятник охотится в основном на мелких лесных птиц размером от королька до кукушки. Иногда его жертвами становятся и птенцы тетерева и рябчика.', 6, NULL, 0.3, NULL, null),
('Ястреб тетеревятник', 'Accipifer genfilis L.', '', 'Ястребов отличают по сравнительно широким коротким крыльям и длинному хвосту. Нижняя часть тела у них серовато-белая, с темными поперечными полосами. Верх тела окрашен в серовато-бурый цвет. Охристые каемки перьев спины и кроющих перьев крыла образуют своеобразный рисунок.', 6, NULL, NULL, NULL, NULL),
('полевой лунь', 'Cireus eyaneus L.', '', 'Самец полевого луня может быть определен по чисто-белому оперению на нижней части тела и темно-серому верху. В полете у него ясно различимы черные концы крыльев. Самка окрашена несколько темнее. На груди у нее заметны продольные пестрины, а на хвосте и концах крыльев — неясные поперечные полосы.', 6, NULL, NULL, NULL, NULL),
('камышовый, или болотный лунь', 'Cireus aeruginouss L', '', 'Самец болотного луня похож на самку полевого, но концы крыльев у него темнее, а хвост светлый, без полос. Самка — бурая, верхняя часть головы и горло у нее окрашены в желтовато-охристый цвет.', 6, NULL, NULL, NULL, NULL),
('луговой лунь', 'Cireus pygargus L', '', '', 6, NULL, NULL, NULL, NULL),
('Черный коршун', 'Milvus korschun Gm.', '', 'На побережьях озер и крупных рек Карелии иногда встречается довольно крупный хищник с треугольной вырезкой на хвосте. Это черный коршун. Оперение верхней части тела у него темно-бурое, а низ рыжеватый, с продольными бурыми пестринами.', 6, NULL, NULL, NULL, NULL),
('Канюк', 'Buteo bufeo L.', '', 'Канюк — довольно крупная птица. Размах его крыльев достигает 1,2 м, а вес — около 700 г. Окраска весьма изменчива. Обычно на спинной стороне преобладают тем- но-бурые тона, а низ бывает охристым или бурым, с неясным поперечным рисунком. Хвост — поперечно-полосатый. В полете на конце широкого округлого хвоста заметна темная полоса, а на широких крыльях — светлые пятна. Крик — протяжное, гнусавое «киий».', 6, NULL, NULL, NULL, NULL),
('мохноногий, или зимняк', 'Buteo lagopus Pontopp', '', 'Он несколько крупнее канюка и отличается от него значительно более светлой окраской и оперенными ногами', 6, NULL, NULL, NULL, NULL),
('Осоед', 'Perrtis apivorus L', '', 'В полете осоед напоминает канюка, но отличается от него двумя полосами на хвосте.', 6, NULL, NULL, NULL, NULL),
('Орлан-белохвост', 'Haliaeetus albicilla L.', '', 'Очень крупная птица. Вес самок орлана, а они несколько крупнее самцов, достигает 5 кг; размах крыльев — более 2 м.', 6, NULL, 5, NULL, NULL),
('Беркут', 'Aquila сИгу5аё1о L.', '', 'Крупный орел. Размах крыльев достигает 2 м, а вес — 4 кг и более.', 6, NULL, 4, NULL, NULL),
('Большой подорлик', 'Aquila clanga Pali.', '', '', 6, NULL, NULL, NULL, NULL),
('Скопа', 'Pasidson Siallaefys L.', '', 'Крупная птица, по размерам лишь немного уступающая орлам и белохвосту. Размах крыльев — более 1,5 м, вес — около 1,5 кг. В окраске скопы много белого цвета. Верх тела бурый, голова, задняя часть шеи и низ — белые. На маховых перьях отчетливо выражены светлые поперечные полосы. На груди и брюхе — рыжевато-бурый поперечный рисунок. Ноги желтые, с длинными округлыми в поперечнике когтями.', 6, NULL, NULL, NULL, NULL),
('сапсан', 'Falco peregrinus Tunsi', '', '', 6, NULL, NULL, NULL, NULL),
('кречет', 'Falco gyrfalco L.', '', '', 6, NULL, NULL, NULL, NULL),
('Чеглок', 'Hypofriorchis subbufeo L.', '', '', 6, NULL, NULL, NULL, NULL),
('Дербник', 'Aesalon columbarius L.', '', 'Небольшой соколок, отличающийся укороченными крыльями.', 6, NULL, NULL, NULL, NULL),
('Пустельга', 'Cerchneis finnunculus L.', '', '', 6, NULL, NULL, NULL, NULL),
('кобчика', 'Eryfhr©pus vesperfinus L', '', '', 6, NULL, NULL, NULL, NULL)
